<script>
/* eslint-disable */
import AppMixin from '../../../mixins/AppMixin'
import Api from '../../../router/api'
export default {
  name: 'Sidebar',
  data() {
    return {
      path: '',
      hideFooter: true,
      createGroup: {
        user: [],
        name: '',
        disabled: false
      },
      chatGroups: [],
      inactivityTimeout: null
    }
  },
  mounted() {
    // Set up event listeners for user activity
    window.addEventListener('mousemove', this.resetInactivityTimer);
    window.addEventListener('keydown', this.resetInactivityTimer);
    window.addEventListener('scroll', this.resetInactivityTimer);
    
    // Start the inactivity timer
    this.startInactivityTimer();
  },
  beforeDestroy() {
    // Clean up event listeners and timers
    window.removeEventListener('mousemove', this.resetInactivityTimer);
    window.removeEventListener('keydown', this.resetInactivityTimer);
    window.removeEventListener('scroll', this.resetInactivityTimer);
    clearTimeout(this.inactivityTimeout);
  },
  mixins: [AppMixin],
  methods: {
    createChatGroup: function () {
      let that = this;
      if (!that.createGroup.name) {
        this.$swal({
          icon: "error",
          title: "error",
          text: "Please enter group name",
          showConfirmButton: true
        })
      } else if (!that.createGroup.user.length) {
        this.$swal({
          icon: "error",
          title: "error",
          text: "Please select atleast one group member",
          showConfirmButton: true
        })
      } else {
        Api.createChatGroup(that.createGroup).then(response => {
          that.createGroup.disabled = false
          this.$swal({
            icon: "success",
            title: "Success",
            text: "Group created successfully",
            showConfirmButton: true
          }).then(function () {
            that.createGroup.disabled = false
            that.createGroup.user = []
            that.createGroup.name = ''
            that.$bvModal.hide('create-group-modal')
            that.getChatGroups();
          });
        }).catch((error) => {
          this.$swal({
            icon: "error",
            title: "error",
            text: error.response.data.message,
            showConfirmButton: true
          }).then(function () {
            that.createGroup.disabled = false
          });
        });
      }
    },
    isCurrentPath(path) {
      return this.$route.path === path;
    },
    resetInactivityTimer() {
      // Reset the inactivity timer
      clearTimeout(this.inactivityTimeout);
      this.startInactivityTimer();
    },
    startInactivityTimer() {
      // Start the inactivity timer
      this.inactivityTimeout = setTimeout(() => {
        // Call your method here for 1 hr inactivity
        this.myMethod();
      }, 3600000); // 1 hour = 60 minutes * 60 seconds * 1000 milliseconds
    },
    myMethod() {
      // Your method logic for handling 1 hr inactivity
      //console.log('1 hr of inactivity');
      //alert('1 hr of inactivity');
      this.logout();
    }
    
  },
  created() {
    if (this.isLoggedIn) {
      this.getEmployeesListChat()
      this.getAuthUser();
      this.getChatGroups();
      window.Echo.channel('chat' + this.user.id)
        .listen('MessageSent', (e) => {
          this.getEmployeesListChat()
        });
      window.Echo.channel('group' + this.user.id)
        .listen('GroupMessageSent', (e) => {
          this.getChatGroups()
        });
    }
  },
}
</script>
